functions:
function deposit(
 DepositParams memory d
 )
 external
 payable
 override
 nonReentrant
 tokenNotBlacklisted(d.token)
 initializeToken(d.token)
 onlyEmergencyDisabled
 {
 _deposit(d, msg.value, msg.sender);
 }

function _deposit(
 DepositParams memory d,
 uint256 _value,
 address tokens_owner
 ) internal drainGas {
 MultiVaultStorage.Storage storage s = MultiVaultStorage._storage();
 uint fee = _calculateMovementFee(
 d.amount,
 d.token,
 IMultiVaultFacetFees.Fee.Deposit
 );
 bool isNative = s.tokens_[d.token].isNative;

 address token = s.tokens_[d.token].custom == address(0) ? d.token : s.tokens_[d.token].custom;
 if (isNative) {
 IMultiVaultToken(token).burn(
 msg.sender,
 d.amount
 );
 d.amount -= fee;
 _transferToEverscaleNative(d, fee, msg.value);
 } else {
 if (tokens_owner != address(this)) {
 IERC20(token).safeTransferFrom(
 tokens_owner,
 address(this),
 d.amount
 );
 }
 d.amount -= fee;
 _transferToEverscaleAlien(d, fee, _value);
 }
 _increaseTokenFee(d.token, fee);
 }

function _withdraw(
 address recipient,
 uint amount,
 uint fee,
 IMultiVaultFacetTokens.TokenType tokenType,
 bytes32 payloadId,
 address token
 ) internal {
 if (tokenType == IMultiVaultFacetTokens.TokenType.Native) {
 IMultiVaultToken(token).mint(recipient, amount - fee);
 } else {
 IERC20(token).safeTransfer(recipient, amount - fee);
 }
 emit Withdraw(
 tokenType,
 payloadId,
 token,
 recipient,
 amount,
 fee
 );
 }

function receive(uint256 fromChainId, address to, uint256 nonce, uint256 volume, Signature[] memory signatures) virtual external payable {
        _chargeFee();
        require(received[fromChainId][to][nonce] == 0, 'withdrawn already');
        uint N = signatures.length;
        require(N >= MappingTokenFactory(factory).getConfig(_minSignatures_), 'too few signatures');
        for(uint i=0; i<N; i++) {
            for(uint j=0; j<i; j++)
                require(signatures[i].signatory != signatures[j].signatory, 'repetitive signatory');
            bytes32 structHash = keccak256(abi.encode(RECEIVE_TYPEHASH, fromChainId, to, nonce, volume, signatures[i].signatory));
            bytes32 digest = keccak256(abi.encodePacked("\x19\x01", _DOMAIN_SEPARATOR, structHash));
            address signatory = ecrecover(digest, signatures[i].v, signatures[i].r, signatures[i].s);
            require(signatory != address(0), "invalid signature");
            require(signatory == signatures[i].signatory, "unauthorized");
            _decreaseAuthQuota(signatures[i].signatory, volume);
            emit Authorize(fromChainId, to, nonce, volume, signatory);
        }
        received[fromChainId][to][nonce] = volume;
        _receive(to, volume);
        emit Receive(fromChainId, to, nonce, volume);
    }
