function _processMessageFromBridge(bytes memory data) internal override {
 (address rootToken, address bridgeToken, address to, uint256 amount) = abi.decode(
 data,
 (address, address, address, uint256)
 );

 require(rootToBridgeTokens[rootToken] == bridgeToken, "FxERC20RootTunnel: INVALID_MAPPING_ON_EXIT");

 IERC20(rootToken).safeTransfer(to, amount);
 emit FxWithdrawERC20(rootToken, bridgeToken, to, amount);
 }

function swapAndStartBridgeTokensViaCBridge(
        LiFiData memory _lifiData,
        LibSwap.SwapData[] calldata _swapData,
        CBridgeData memory _cBridgeData
    ) public payable {
        if (_cBridgeData.token != address(0)) {
            uint256 _fromTokenBalance = LibAsset.getOwnBalance(_cBridgeData.token);

            for (uint8 i; i < _swapData.length; i++) {
                LibSwap.swap(_lifiData.transactionId, _swapData[i]);
            }
            uint256 _postSwapBalance = LibAsset.getOwnBalance(_cBridgeData.token) - _fromTokenBalance;
            require(_postSwapBalance > 0, "ERR_INVALID_AMOUNT");
            _cBridgeData.amount = _postSwapBalance;
        } else {
            uint256 _fromBalance = address(this).balance;

            for (uint8 i; i < _swapData.length; i++) {
                LibSwap.swap(_lifiData.transactionId, _swapData[i]);
            }
            uint256 _postSwapBalance = address(this).balance - _fromBalance;
            require(_postSwapBalance > 0, "ERR_INVALID_AMOUNT");
            _cBridgeData.amount = _postSwapBalance;
        }
        _startBridge(_cBridgeData);
        emit LiFiTransferStarted(
            _lifiData.transactionId,
            _lifiData.integrator,
            _lifiData.referrer,
            _lifiData.sendingAssetId,
            _lifiData.receivingAssetId,
            _lifiData.receiver,
            _lifiData.amount,
            _lifiData.destinationChainId,
            block.timestamp
        );
    }
