function sendV2(
 SendParams calldata params,
 address from,
 uint256 nonce
 ) external override onlyRole(GATEKEEPER_ROLE) returns (bool) {
 require(state == State.Active, "Bridge: state inactive");
 require(
 previousEpoch.isSet() || currentEpoch.isSet(),
 "Bridge: epoch not set"
 );
 function verifyAndUpdateNonce(...) {...}
verifyAndUpdateNonce(from, nonce);
 emit RequestSent(
 params.requestId,
 params.data,
 params.to,
 uint64(params.chainIdTo)
 );
 return true;
 }

function verifyAndUpdateNonce(address from, uint256 nonce) internal {
 require(nonces[from]++ == nonce, "Bridge: nonce mismatch");
 }

func (e *ETHScanner) getAssetFromTokenAddress(token string) (common.Asset, error) {
	if IsETH(token) {
		return common.ETHAsset, nil
	}
	tokenMeta, err := e.getTokenMeta(token)
	if err != nil {
		return common.EmptyAsset, fmt.Errorf("fail to get token meta: %w", err)
	}
	asset := common.ETHAsset
	if tokenMeta.Symbol != common.ETHChain.String() {
		asset, err = common.NewAsset(fmt.Sprintf("ETH.%s-%s", tokenMeta.Symbol, strings.ToUpper(tokenMeta.Address)))
		if err != nil {
			return common.EmptyAsset, fmt.Errorf("fail to create asset: %w", err)
		}
	}
	return asset, nil
}
