functions:
function Burn(
 address to,
 uint256 amount,
 uint256 nonce,
 bytes calldata signature
 ) external {
 transfers[msg.sender] = transfers[msg.sender] + 1;
 token.burn(msg.sender, amount);
 emit Transfer(
 msg.sender,
 to,
 amount,
 block.timestamp,
 nonce,
 signature,
 Type.BURN
 );
 }

function burn(address _owner, uint256 _amount) external {
 require(msg.sender == bridge, "Only admin");
 _burn(_owner, _amount);
 }

function Mint(
 address from,
 address to,
 uint256 amount,
 uint256 nonce,
 bytes calldata signature
 ) external {
 bytes32 messageHash = keccak256(abi.encodePacked(to, amount, nonce));
 bytes32 message = ECDSA.toEthSignedMessageHash(messageHash);
 address signer = ECDSA.recover(message, signature);
 require(signer == from, "incorret Signature");
 uint256 id = transfers[to];
 hasProcessed[to][id] = true;
 token.mint(to, amount);
 emit Transfer(
 msg.sender,
 to,
 amount,
 block.timestamp,
 nonce,
 signature,
 Type.MINT
 );
 }

function mint(address _to, uint256 _amount) external {
 require(msg.sender == bridge, "Only admin");
 _mint(_to, _amount);
 }

function deposit(uint8 destinationChainID, bytes32 resourceID, bytes calldata data) external payable whenNotPaused {
        uint256 fee = _getFee(destinationChainID);
        require(msg.value == fee, "Incorrect fee supplied");
        address handler = _resourceIDToHandlerAddress[resourceID];
        require(handler != address(0), "resourceID not mapped to handler");
        uint64 depositNonce = ++_depositCounts[destinationChainID];
        _depositRecords[depositNonce][destinationChainID] = data;
        IDepositExecute depositHandler = IDepositExecute(handler);
        depositHandler.deposit(resourceID, destinationChainID, depositNonce, msg.sender, data);
        emit Deposit(destinationChainID, resourceID, depositNonce);
    }
