functions:
function synthesize(
 uint256 _stableBridgingFee,
 address _token,
 uint256 _amount,
 address _chain2address,
 address _receiveSide,
 address _oppositeBridge,
 address _revertableAddress,
 uint256 _chainID,
 bytes32 _clientID
 ) external whenNotPaused returns (bytes32) {
 require(tokenWhitelist[_token], "Symb: unauthorized token");
 require(_amount >= tokenThreshold[_token], "Symb: amount under threshold");
 TransferHelper.safeTransferFrom(
 _token,
 _msgSender(),
 address(this),
 _amount
 );
 return
 sendSynthesizeRequest(
 _stableBridgingFee,
 _token,
 _amount,
 _chain2address,
 _receiveSide,
 _oppositeBridge,
 _revertableAddress,
 _chainID,
 _clientID
 );
 }

function sendSynthesizeRequest(
 uint256 _stableBridgingFee,
 address _token,
 uint256 _amount,
 address _chain2address,
 address _receiveSide,
 address _oppositeBridge,
 address _revertableAddress,
 uint256 _chainID,
 bytes32 _clientID
 ) internal returns (bytes32 internalID) {
 balanceOf[_token] = balanceOf[_token] + _amount;
 if (_revertableAddress == address(0)) {
 _revertableAddress = _chain2address;
 }
 internalID = keccak256(abi.encodePacked(this, requestCount, block.chainid));
 {
 bytes32 externalID = keccak256(abi.encodePacked(internalID, _receiveSide, _revertableAddress, _chainID));
 {
 bytes memory out = abi.encodeWithSelector(
 bytes4(
 keccak256(
 bytes(
 "mintSyntheticToken(uint256,bytes32,address,uint256,uint256,address)"
 )
 )
 ),
 _stableBridgingFee,
 externalID,
 _token,
 block.chainid,
 _amount,
 _chain2address
 );
 requests[externalID] = TxState({
 recipient : _msgSender(),
 chain2address : _chain2address,
 rtoken : _token,
 amount : _amount,
 state : RequestState.Sent
 });
 requestCount++;
 IBridge(bridge).transmitRequestV2(
 out,
 _receiveSide,
 _oppositeBridge,
 _chainID
 );
 }
 }
 emit SynthesizeRequest(
 internalID,
 _msgSender(),
 _chainID,
 _revertableAddress,
 _chain2address,
 _amount,
 _token
 );
 emit ClientIdLog(internalID, _clientID);
 }

function _transfer(
 address sender,
 address recipient,
 uint256 amount
 ) internal virtual {
 require(sender != address(0), "ERC20: transfer from the zero address");
 require(recipient != address(0), "ERC20: transfer to the zero address");
 _beforeTokenTransfer(sender, recipient, amount);
 uint256 senderBalance = _balances[sender];
 require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
 unchecked {
 _balances[sender] = senderBalance - amount;
 }
 _balances[recipient] += amount;
 emit Transfer(sender, recipient, amount);
 _afterTokenTransfer(sender, recipient, amount);
 }

function swap(
        Swap storage self,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx,
        uint256 minDy
    ) external returns (uint256) {
        {

            IERC20 tokenFrom = self.pooledTokens[tokenIndexFrom];
            require(
                dx <= tokenFrom.balanceOf(msg.sender),
                "Cannot swap more than you own"
            );

            uint256 beforeBalance = tokenFrom.balanceOf(address(this));     
            tokenFrom.safeTransferFrom(msg.sender, address(this), dx);      

            dx = tokenFrom.balanceOf(address(this)).sub(beforeBalance);     
        }
        uint256 dy; 
        uint256 dyFee;  
        uint256[] memory balances = self.balances;

        (dy, dyFee) = _calculateSwap(
            self,
            tokenIndexFrom,
            tokenIndexTo,
            dx,
            balances
        );
        require(dy >= minDy, "Swap didn't result in min tokens");   
        uint256 dyAdminFee =
            dyFee.mul(self.adminFee).div(FEE_DENOMINATOR).div(
                self.tokenPrecisionMultipliers[tokenIndexTo]
            );  

        self.balances[tokenIndexFrom] = balances[tokenIndexFrom].add(dx);

        self.balances[tokenIndexTo] = balances[tokenIndexTo].sub(dy).sub(
            dyAdminFee
        );

        self.pooledTokens[tokenIndexTo].safeTransfer(msg.sender, dy);

        emit TokenSwap(msg.sender, dx, dy, tokenIndexFrom, tokenIndexTo);
        return dy;
    }
